sysauto_script.script:
//only return NeedIt Task records where the State field does not equal Closed Complete
//only return NeedIt Task records where the dude date i between now and 24 hours from now

//GEt current time
var rightNow = new GlideDateTime();

//get time 24 hours in the future
var futureTime = new GlideDateTime();
futureTime.addDaysLocalTime(1);

//query the NeedIt Task table
var NeedItTasksDueSoon = new GlideRecord('x_58872_needit_needit_task');
NeedItTasksDueSoon.addQuery('due_date','>=',rightNow);
NeedItTasksDueSoon.addQuery('due_date','<=',futureTime);
NeedItTasksDueSoon.addQuery('state','!=',3);

NeedItTasksDueSoon.query();

while (NeedItTasksDueSoon.next()) {
	gs.info('Record of NeedIt Task due within next 24 hours is: ' + NeedItTasksDueSoon.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',NeedItTasksDueSoon,NeedItTasksDueSoon.number,gs.getUserName());
}
